package io.swagger.repository;import io.swagger.model.Book;import org.springframework.data.repository.CrudRepository;import org.springframework.stereotype.Repository;import java.time.LocalDate;@Repositorypublic interface BookApiRepository extends CrudRepository<Book,String> {    Iterable<Book> findByTitle(String title);    Iterable<Book> findByAuthor(String name);    Iterable<Book> findByPublisher(String publisher);}